import React from "react";
import axios from "axios";

class BoardMap extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: [],
      cells: [],
      gridSize: props.gridSize,
      numberOfBombs: props.numberOfBombs
    };

    // this.startGame = this.startGame.bind(this);
  }

  componentDidMount() {
    this.handleServerRequest("get");
  }

  startGame() {
    this.handleServerRequest("post");
  }

  handleServerRequest(requestType) {
    switch (requestType) {
      case "post":
        return axios
          .post("http://localhost:8000/api/", {
            gridSize: this.state.gridSize,
            numberOfBombs: this.state.numberOfBombs
          })
          .then(res => {
            console.log(res.data);
            this.setState({
              cells: res.data
            });
          })
          .catch(err => {
            console.err(err);
          });
      case "get":
        return axios.get("http://localhost:8000/api/").then(res => {
          console.log(res.data);
          if (res.data == "") this.startGame();
          else
            this.setState({
              cells: res.data
            });
        });
    }
  }

  render() {
    return <div className="boardmap" />;
  }
}

export default BoardMap;
