import React from "react";
import ReactDOM from "react-dom";
import BoardMap from "./components/boardmap";
import Header from "./components/header";

import "./minsweeper.css";
class Minesweeper extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      gridSize: 9,
      numberOfBombs: 20,
      timer: 0,
      flagCount: 20,
      gameStatus: "waiting"
    };
    this.intervals = [];
  }
  state = {
    gridSize: 9,
    numberOfBombs: 20,
    timer: 0,
    flagCount: 20,
    gameStatus: "waiting"
  };

  startTimer = () => {
    this.setState(
      {
        gameStatus: "running"
      },
      this.setInterval(this.timer, 1000)
    );
  };
  setInterval = (fn, t) => {
    this.intervals.push(setInterval(fn, t));
  };

  timer = () => {
    let time = this.state.time + 1;
    this.setState({ timer: time });
  };
  render() {
    return (
      <div className="minesweeper">
        <Header timer={this.state.timer} flagCount={this.state.flagCount} />
        <BoardMap
          gridSize={this.state.gridSize}
          numberOfBombs={this.state.numberOfBombs}
          gameStatus={this.state.gameStatus}
          onStartTimer={this.startTimer}
        />
      </div>
    );
  }
}

ReactDOM.render(<Minesweeper />, document.querySelector("#root"));
